<?php/* Template Name: Overview Page Template */get_header();?><?php if( get_field('header_image') ): ?>    <?php $image = get_field('header_image'); ?>    <div class="header-image">        <img src="<?php echo $image['url']; ?>" alt="">    </div><?php endif; ?><?phpglobal $current_custom_user;$current_user_field = 'user_' . $current_custom_user;global $wpdb;$current_title = esc_html(get_the_title());$sql = "SELECT item_tracking.Option, bank_feed.Date, bank_feed.Reference, ABS(bank_feed.Amount) as Total, bank_feed.Amount as Original_Total,bank_feed.Reconciled,bank_feed.Balance, month(bank_feed.Date) as date_month, year(bank_feed.Date) as date_year FROM xero_expense AS expense RIGHT JOIN xero_expense_lineitem as lineitem ON lineitem.BankTransactionID = expense.BankTransactionID RIGHT JOIN xero_expense_item_tracking as item_tracking ON item_tracking.LineItemID = lineitem.LineItemID RIGHT JOIN xero_bank_feed as bank_feed ON (expense.Reference = '' and convert(bank_feed.Date,DATE) = convert(expense.Date,DATE) and ABS(bank_feed.Amount) = expense.Total) or (bank_feed.Reference = expense.Reference AND convert(bank_feed.Date,DATE) = convert(expense.Date,DATE))";$bank_feed = $wpdb->get_results($sql);$templateData = array();foreach ( $bank_feed as $bank_feed_key => $bank_feed_value ){    $templateData[$bank_feed_value->date_year][$bank_feed_value->date_month][] = $bank_feed_value;}//  menu_list$sql =  "SELECT item_track.Option as name,         SUM(lineitem.LineAmount) as CountValue         FROM xero_expense_lineitem AS lineitem         LEFT JOIN xero_expense_item_tracking as item_track ON lineitem.LineItemID = item_track.LineItemID         LEFT JOIN xero_expense as expense ON expense.BankTransactionID = lineitem.BankTransactionID         WHERE expense.Type = 'SPEND'        GROUP BY item_track.Option";$result = $wpdb->get_results($sql);$totVal = 0;?>    <div class="main-container" style="background-image: url(<?php the_field('header_background'); ?>)">        <div class="wrapper overflow main_projects">            <main role="main">                <div class="tabs project_tabs main_menu">                    <ul id="tabs">                        <li>                            <a id="tab1">                                <h2 class="title wow animated fadeIn">                                    <?php the_title(); ?>                                </h2>                            </a>                        </li>                        <li>                            <a id="tab2">                                <div class="title wow animated fadeIn">                                    <p>Bank Feed</p>                                </div>                            </a>                        </li>                        <li>                            <div class="main_menu wow animated fadeIn">                                <ul>                                    <li class="dropdown">                                        <p>Expense Summary &#9662;</p>                                        <div class="dropdown-menu">                                            <div class="list_top_section">                                                <span class="pro_title">Project</span>                                                <span class="pro_spend">Spend</span>                                            </div>                                            <ul>                                                <?php                                                $s = 0;                                                $uncat = false;                                                $len = count($result);                                                if($result){                                                    foreach ($result as $key => $values ){                                                        if($values->name == '' || $values->name == NULL){                                                            $uncat = false;                                                            break;                                                        }else if($s == $len - 1){                                                            $uncat = true;                                                        }                                                        ?>                                                 <?php $s++; }}?>                                                <?php                                                if($result){                                                    foreach ($result as $key => $values ){                                                        $totVal +=$values->CountValue; ?>                                                        <?php if($uncat == true){                                                            $uncat = false; ?>                                                            <li class="items_inner">                                                                <span class="pro_title">Un-categorised</span>                                                                <span class="pro_spend">$0</span>                                                            </li>                                                         <?php } ?>                                                        <li class="items_inner">                                                            <span class="pro_title"><?php echo $values->name?$values->name:'Un-categorised'; ?></span>                                                            <span class="pro_spend"><?php echo $values->CountValue? '$'. number_format($values->CountValue,2,'.',','): '0$'; ?></span>                                                        </li>                                                    <?php }} ?>                                            </ul>                                            <div class="list_bm_section">                                                <span class="pro_title">Total Spend</span>                                                <span class="pro_spend"><?php echo $totVal ? '$'. number_format($totVal,2,'.',','): '0$' ; ?></span>                                            </div>                                        </div>                                    </li>                                </ul>                            </div>                        </li>                    </ul>                    <div class="container" id="tab1C"><?php get_template_part('loop-clients'); ?></div>                    <div class="container" id="tab2C">                        <?php                        if(count($templateData)){ ?>                            <div class="panel panel-default list_table">                                <div class="panel-body">                                    <table class="table table-condensed">                                        <thead>                                        <tr>                                            <th style="text-align:left;">Date</th>                                            <th style="text-align:left;">Project</th>                                            <th style="text-align:left;width:35%;">Reference</th>                                            <th style="text-align:center;">Debit</th>                                            <th style="text-align:center;">Credit</th>                                            <th style="text-align:center;">Balance</th>                                            <th style="text-align:right;">Status</th>                                        </tr>                                        </thead>                                        <tbody>                                        <?php                                        $total_balance = 0;                                        foreach ($templateData as $year => $months ){                                            foreach ($months as $month => $listDatas){                                                $dateObj   = DateTime::createFromFormat('!m', $month);                                                $monthName = $dateObj->format('M');                                                $cur_month = date('n');                                                $cur_year = date('Y');                                                ?>                                                <tr>                                                    <td colspan="12" class="main_head_bg">                                                        <div role="button" data-toggle="collapse" href="#list_<?php echo $year.'_'.$month?>"                                                             class="accordion-toggle collapsed" aria-expanded="true">                                                            <span class="text_right"><?php echo $monthName, ' ', $year; ?></span>                                                        </div>                                                    </td>                                                </tr>                                                <tr>                                                    <td colspan="12" class="hiddenRow">                                                        <div class="accordian-body collapse <?php if(($cur_month == $month) && ($cur_year == $year)){echo 'show';} ?>" id="list_<?php echo $year.'_'.$month?>">                                                            <table class="table">                                                                <tbody>                                                                <?php                                                                for($i=count($listDatas)-1; $i>=0; $i=$i-1){                                                                    $listData = $listDatas[$i];                                                                    $total_balance+=$listData->Balance;                                                                    ?>                                                                    <tr>                                                                        <td style="text-align:left;">                                                                            <?php                                                                            $date=date_create($listData->Date);                                                                            echo date_format($date,"d M Y") ? date_format($date,"d M Y") : '';                                                                            ?>                                                                        </td>                                                                        <td style="text-align:left;"><?php echo $listData->Option;?> </td>                                                                        <td style="text-align:left;width:35%;"><?php echo $listData->Reference;?> </td>                                                                        <td style="text-align:right;">                                                                            <?php if ($listData->Original_Total < 0 ){                                                                                echo $listData->Total ? '$' . number_format($listData->Total, 2, '.', ',') : '$0'; ?>                                                                            <?php } ?>                                                                        </td>                                                                        <td style="text-align:right;">                                                                            <?php if ($listData->Original_Total >= 0){                                                                                echo $listData->Total ? '$' . number_format($listData->Total, 2, '.', ',') : '$0'; ?>                                                                            <?php } ?>                                                                        </td>                                                                        <td style="text-align:right;">                                                                            <?php                                                                            echo $listData->Balance ? '$' . number_format($listData->Balance, 2, '.', ',') : '$0';                                                                            ?>                                                                        </td>                                                                        <td style="text-align:right;" class="<?php echo $listData->Reconciled == 'Yes' ? 'recon ':'unrecon' ?>">                                                                            <?php                                                                            echo $listData->Reconciled == 'Yes' ? 'Reconciled ':'Unreconciled';                                                                            ?>                                                                        </td>                                                                    </tr>                                                                <?php } ?>                                                                </tbody>                                                            </table>                                                        </div>                                                    </td>                                                </tr>                                            <?php }} ?>                                        <tr class="amount_cal">                                            <!--<td></td>                                            <td></td>                                            <td style="width:35%;"></td>                                            <td style="text-align:right;"></td>                                            <td style="text-align:right;"><p class="amount_cal_style">Closing Balance</p></td>                                            <td style="text-align:right;">                                                <p class="amount_cal_style"><?php /*echo $total_balance? '$' . $total_balance : '$0'; */?></p>                                            </td>                                            <td style="text-align: right;"></td>-->                                        </tr>                                        </tbody>                                    </table>                                </div>                            </div>                        <?php }else{ ?>                            <p>Bank feed data not found.</p>                        <?php }?>                    </div>                </div>            </main>        </div>    </div>    <script>        jQuery(document).ready(function(){            jQuery(".main_menu .dropdown").click(function(){                jQuery(this).find(".dropdown-menu").slideToggle("fast");            });            jQuery('#tabs li:first-child a').addClass('isActive');            jQuery('#tabs li a:not(:first)').addClass('inactive');            jQuery('.container').hide();            jQuery('.container:first').show();            jQuery('#tabs li a').click(function(){                var t = jQuery(this).attr('id');                if(jQuery(this).hasClass('inactive')){ //this is the start of our condition                    jQuery('#tabs li a').addClass('inactive');                    jQuery('#tabs li a').removeClass('isActive');                    jQuery(this).removeClass('inactive');                    jQuery(this).addClass('isActive');                    jQuery('.container').hide();                    jQuery('#'+ t + 'C').fadeIn('slow');                }            });        });        jQuery(document).on("click", function(event){            var $trigger = jQuery(".dropdown");            if($trigger !== event.target && !$trigger.has(event.target).length){                jQuery(".dropdown-menu").slideUp("fast");            }        });    </script><style>    .dropdown-menu ul::-webkit-scrollbar-track {        background-color: white;    }    .dropdown-menu ul::-webkit-scrollbar {        width: 12px;        background-color:white;    }    .dropdown-menu ul::-webkit-scrollbar-thumb{        border-radius: 10px;        -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,.3);        background-color: #bbb;    }    .main_menu .dropdown .dropdown-menu{        box-shadow: 2px 2px 4px 0px rgba(0,0,0,0.3);    }</style>    <script src="<?php echo get_template_directory_uri();?>/js/bootstrap.min.js"></script><?php get_footer(); ?>