<?php/* Template Name: Xero Template */get_header();?><?phpglobal $wpdb;$tok_res = $wpdb->get_results("SELECT * FROM `xero_token`", ARRAY_A);?><div style="display: none;" id="loader-wrapper">    <div id="loader"></div></div><div class="main-container xero_main">    <div class="projects-section">        <div class="wrapper overflow xero_sec">            <h3>Xero Connection</h3>            <div class="logos">                <ul>                    <li class="litt_wid">                        <img title="little fish" src="<?php echo get_template_directory_uri(); ?>/img/little_fish_logo.png"/>                    </li>                    <li class="check_wid">                        <?php if(count($tok_res)){ ?>                            <span class="xero_hype_left">---</span>                            <div class="checkmark">                                <div class="checkmark_circle"></div>                                <div class="checkmark_stem"></div>                                <div class="checkmark_kick"></div>                            </div>                            <span class="xero_hype_right">---</span>                        <?php }else{ ?>                            <div class="close_icon">                                <span class="xero_hype_left">---</span>                                <span class="close-icon"></span>                                <span class="xero_hype_right">---</span>                            </div>                        <?php } ?>                    </li>                    <li class="xero_wid">                        <img title="xero" src="<?php echo get_template_directory_uri(); ?>/img/xero_logo.png"/>                    </li>                    <li class="xero_button">                        <?php if(count($tok_res)){ ?>                            <button class="dis_connect_button">Disconnect with Xero</button>                       <?php }else{ ?>                            <span class="xero_hidden" data-xero-sso data-label="Connect to Xero" id="target"></span>                        <?php } ?>                    </li>                </ul>            </div>        </div>    </div></div><script type="text/javascript">    jQuery(document).ready(function() {        jQuery(document).on("click",".dis_connect_button",function() {            jQuery("#loader-wrapper").show();            var ajaxurl = "<?php echo admin_url('admin-ajax.php'); ?>";            var data = {                action: 'xero_disconnect'            };            jQuery.ajax({                type: 'post',                url: ajaxurl,                data: data,                success: function (response) {                    console.log(response);                    if(response == 'records_cleared'){                        jQuery("#loader-wrapper").hide();                        window.location.href = window.location.href;                    }                }            });        });    });</script><script src="https://edge.xero.com/platform/sso/xero-sso.js" async defer></script><?php get_footer(); ?>